var domify = require('domify')
var render = require('templayd')

function Component (parent, template) {
  var self = this
  self.node = null
  this.draw = function (data) {
    self.node = domify(render(template, data))
    parent.appendChild(self.node)
  }
  this.erase = function () {
    parent.removeChild(self.node)
  }
}

module.exports = function (parent, template) {
  return new Component(parent, template)
}
