var test = require('tape')
var tpl = require('../index.js')

test('get part of the template', function (t) {
  t.plan(3)
  var markup = '<ul class="data"></ul><li class="item">{{txt}}</li>'
  var data = {txt:'fake data'}
  var ul = tpl(document.body, markup) 

  var li = tpl(document.body, ul.get('.item'))

  t.equal(ul.get('.item'),'<li class="item">{{txt}}</li>')

  li.draw(data, function (el) {
    t.equal(el.innerHTML, data.txt)
    var item = document.body.querySelector('.item')
    t.equal(item.tagName,'LI')
  })
})
