var through = require('through2')

module.exports = function (C) {
  var delay = 1500

  // align element with targeted interface / input
  var s = through.obj(function Write (d, enc, next) {
    // examine d.value and move from there! 
    // draw beside target element!
    
    C.draw(d)

    setTimeout(function () {
      C.erase()
    }, delay)

    next()
  })

  return s
}
