module.exports.cuid = require('cuid') 

module.exports.search = function (haystack, needle) {
  for (hay in haystack) {
    if (hay.match(needle)) return haystack[hay]
  }
}

module.exports.getPath = function () { 
  if (!window.location.hash) return false
  if (window.location.hash) return window.location.hash.slice(1)
}

module.exports.cancel = function (e) {
  e.preventDefault()
  if (e.stopPropogation) e.stopPropogation()
}

module.exports.climb = function (el) {
  var id = null
  var element = el

  while (element.parentElement) {
    if (element.id) {
      id = element.id
      break;
    }
    element = element.parentElement
  }

  return id
}

module.exports.incdec = function (incdec, val, min, max) {
  if (incdec === 'inc') val++
  if (incdec === 'dec') val--
  if (val < min) val = min
  if (val > max) val = max
  return val
}
